const HomePage = ({ currentUser, onLogout }) => {
  const navigate = useNavigate();
  const { isPlaying, musicFiles, currentMusic, toggleMusic, changeMusic } = useAudio();
  const [showPersonDropdown, setShowPersonDropdown] = useState(false);

  const generateHearts = () => {
    const hearts = [];
    for (let i = 0; i < 20; i++) {
      hearts.push({
        id: i,
        x: Math.random() * 100,
        y: Math.random() * 100,
        size: Math.random() * 20 + 10,
        delay: Math.random() * 2
      });
    }
    return hearts;
  };

  return (
    <HomeContainer>
      <BackgroundOverlay />

      <FloatingHearts>
        {generateHearts().map(heart => (
          <Heart
            key={heart.id}
            size={heart.size}
            style={{
              left: `${heart.x}%`,
              top: `${heart.y}%`,
            }}
            animate={{
              y: [-10, 10, -10],
              opacity: [0.3, 0.7, 0.3],
            }}
            transition={{
              duration: 6,
              repeat: Infinity,
              delay: heart.delay,
            }}
          >
            ❤️
          </Heart>
        ))}
      </FloatingHearts>

      {currentUser && (
        <UserInfoContainer>
          <WelcomeMessage>
            Welcome, {currentUser.username}!
          </WelcomeMessage>
        </UserInfoContainer>
      )}

      <MusicControlsContainer>
        {musicFiles.length > 0 && isPlaying && (
          <MusicSelector
            value={currentMusic || ''}
            onChange={(e) => {
              changeMusic(e.target.value);
            }}
          >
            {musicFiles.map(music => (
              <option key={music} value={music}>
                {music.replace(/\.(mp3|wav|m4a)$/i, '')}
              </option>
            ))}
          </MusicSelector>
        )}
        <MusicControl
          onClick={toggleMusic}
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
        >
          {isPlaying ? <FaPause /> : <FaPlay />}
          {isPlaying ? 'Pause' : 'Play'}
        </MusicControl>
        {currentUser && (
          <LogoutButton onClick={onLogout}>
            Logout
          </LogoutButton>
        )}
      </MusicControlsContainer>

      <Content>
        <HeartIcon
          animate={{ scale: [1, 1.1, 1] }}
          transition={{ duration: 2, repeat: Infinity }}
        >
          ❤️
        </HeartIcon>

        <Title
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1, delay: 0.2 }}
        >
          Memory Lane
        </Title>

        <Subtitle
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1, delay: 0.4 }}
        >
          A Journey of Us • Our Love Story in Pictures
        </Subtitle>

        <ButtonContainer
          initial={{ opacity: 0, y: 30 }}
