import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import { FaHeart, FaTimes, FaChevronLeft, FaChevronRight, FaHome, FaPlay, FaPause, FaSync, FaMusic, FaTrash, FaEllipsisV } from 'react-icons/fa';
import axios from 'axios';
import PhotoInteractions from './PhotoInteractions';

const GalleryContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  min-height: 70vh;
  width: 100vw;
  overflow: hidden;
`;

const PhotoViewer = styled.div`
  display: flex;
  flex-direction: row;
  align-items: stretch;
  justify-content: center;
  width: 100vw;
  max-width: 900px;
  min-height: 400px;
  background: rgba(255,255,255,0.08);
  border-radius: 24px;
  box-shadow: 0 8px 32px rgba(0,0,0,0.12);
  margin: 0 auto;
  position: relative;
  overflow: hidden;
  @media (max-width: 900px) {
    max-width: 100vw;
    border-radius: 0;
  }
  @media (max-width: 700px) {
    flex-direction: column;
    min-height: 300px;
  }
`;

const PhotoArea = styled.div`
  flex: 1 1 60%;
  display: flex;
  align-items: center;
  justify-content: center;
  background: transparent;
  @media (max-width: 700px) {
    min-height: 220px;
    max-height: 60vw;
  }
`;

const PhotoImg = styled.img`
  max-width: 100%;
  max-height: 60vh;
  border-radius: 18px;
  box-shadow: 0 4px 16px rgba(0,0,0,0.10);
  @media (max-width: 700px) {
    max-width: 90vw;
    max-height: 40vh;
    border-radius: 12px;
  }
`;

const InteractionsArea = styled.div`
  flex: 1 1 40%;
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: stretch;
  background: transparent;
  padding: 32px 24px;
  @media (max-width: 900px) {
    padding: 24px 10px;
  }
  @media (max-width: 700px) {
    padding: 16px 0 0 0;
    width: 100%;
  }
`;

const NavButton = styled.button`
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  background: rgba(0,0,0,0.18);
  color: white;
  border: none;
  border-radius: 50%;
  width: 44px;
  height: 44px;
  font-size: 2rem;
  cursor: pointer;
  z-index: 2;
  transition: background 0.2s;
  &:hover {
    background: rgba(0,0,0,0.32);
  }
  @media (max-width: 700px) {
    width: 36px;
    height: 36px;
    font-size: 1.5rem;
  }
`;
const PrevButton = styled(NavButton)`
  left: 10px;
`;
const NextButton = styled(NavButton)`
  right: 10px;
`;

const Header = styled(motion.div)`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 30px;
  padding: 20px;
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(10px);
  border-radius: 20px;
  border: 1px solid rgba(255, 255, 255, 0.2);
  flex-wrap: wrap;
  gap: 15px;

  @media (max-width: 768px) {
    flex-direction: column;
    align-items: stretch;
    gap: 16px;
    padding: 16px 12px;
    margin-bottom: 20px;
  }

  @media (max-width: 480px) {
    padding: 12px 8px;
    gap: 12px;
    margin-bottom: 15px;
  }
`;

const Title = styled.h1`
  color: white;
  font-family: 'Pacifico', cursive;
  font-size: 2.2rem;
  font-weight: 400;
  display: flex;
  align-items: center;
  gap: 10px;
  letter-spacing: 1px;
  line-height: 1.1;
  margin: 0;

  @media (max-width: 768px) {
    font-size: 1.8rem;
    justify-content: center;
    text-align: center;
    width: 100%;
  }

  @media (max-width: 480px) {
    font-size: 1.4rem;
    gap: 6px;
  }

  @media (max-width: 360px) {
    font-size: 1.2rem;
  }
`;

const HeaderControls = styled.div`
  display: flex;
  gap: 10px;
  align-items: center;
  flex-wrap: wrap;

  @media (max-width: 768px) {
    justify-content: center;
    gap: 8px;
  }

  @media (max-width: 480px) {
    flex-direction: column;
    width: 100%;
    gap: 10px;
  }
`;

const MusicSelector = styled.select`
  background: rgba(255, 255, 255, 0.2);
  border: 1px solid rgba(255, 255, 255, 0.3);
  color: white;
  padding: 8px 12px;
  border-radius: 15px;
  cursor: pointer;
  font-size: 0.95rem;
  backdrop-filter: blur(10px);
  max-width: 160px;
  text-overflow: ellipsis;
  white-space: nowrap;
  overflow: hidden;
  appearance: none;

  option {
    background: #333;
    color: white;
  }

  &:hover {
    background: rgba(255, 255, 255, 0.3);
  }

  @media (max-width: 768px) {
    font-size: 0.9rem;
    padding: 7px 10px;
    max-width: 140px;
  }

  @media (max-width: 480px) {
    width: 100%;
    max-width: none;
    font-size: 1rem;
    padding: 10px 12px;
  }
`;

const MusicControl = styled(motion.button)`
  background: rgba(255, 255, 255, 0.2);
  border: none;
  color: white;
  width: 40px;
  height: 40px;
  border-radius: 50%;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.3s ease;

  &:hover {
    background: rgba(255, 255, 255, 0.3);
    transform: scale(1.1);
  }

  @media (max-width: 480px) {
    width: 44px;
    height: 44px;
    font-size: 1.1rem;
  }
`;

const GalleryGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(320px, 1fr));
  gap: 32px;
  width: 100%;
  max-width: 1200px;
  margin: 0 auto;
  padding: 32px 0;
  @media (max-width: 700px) {
    grid-template-columns: 1fr;
    gap: 20px;
    padding: 16px 0;
  }
`;

const ImageCard = styled.div`
  background: rgba(255,255,255,0.08);
  border-radius: 18px;
  box-shadow: 0 4px 16px rgba(0,0,0,0.10);
  overflow: hidden;
  display: flex;
  flex-direction: column;
  align-items: stretch;
  position: relative;
`;

const Image = styled.img`
  width: 100%;
  height: 250px;
  object-fit: cover;
  border-top-left-radius: 18px;
  border-top-right-radius: 18px;
  @media (max-width: 700px) {
    height: 180px;
    border-radius: 12px 12px 0 0;
  }
`;

const ImageOverlay = styled.div`
  position: absolute;
  left: 0;
  top: 0;
  width: 100%;
  height: 40px;
  background: linear-gradient(180deg, rgba(0,0,0,0.25) 60%, transparent 100%);
  color: #fff;
  font-size: 1.1rem;
  font-weight: 500;
  padding: 8px 16px;
  z-index: 2;
  pointer-events: none;
`;

const LoadingContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 400px;
  color: white;
  font-size: 1.2rem;
`;

const Lightbox = styled(motion.div)`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.9);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 1000;
  padding: 20px;
`;

const LightboxImage = styled.img`
  max-width: 90%;
  max-height: 90%;
  object-fit: contain;
  border-radius: 10px;
`;

const LightboxControls = styled.div`
  position: absolute;
  top: 20px;
  right: 20px;
  display: flex;
  gap: 10px;
`;

const LightboxButton = styled.button`
  background: rgba(255, 255, 255, 0.2);
  border: none;
  color: white;
  width: 40px;
  height: 40px;
  border-radius: 50%;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.3s ease;

  &:hover {
    background: rgba(255, 255, 255, 0.3);
  }
`;

const NavigationButtons = styled.div`
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  display: flex;
  justify-content: space-between;
  width: 100%;
  padding: 0 20px;
  pointer-events: none;
`;

const NavButtonLightbox = styled.button`
  background: rgba(255, 255, 255, 0.2);
  border: none;
  color: white;
  width: 50px;
  height: 50px;
  border-radius: 50%;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  pointer-events: all;
  transition: all 0.3s ease;

  &:hover {
    background: rgba(255, 255, 255, 0.3);
  }
`;

const PhotoActionsMenu = styled.div`
  position: absolute;
  top: 10px;
  right: 10px;
  z-index: 3;

  @media (max-width: 768px) {
    top: 8px;
    right: 8px;
  }

  @media (max-width: 480px) {
    top: 6px;
    right: 6px;
  }
`;

const MenuButton = styled.button`
  background: rgba(0, 0, 0, 0.6);
  border: none;
  color: white;
  width: 32px;
  height: 32px;
  border-radius: 50%;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 1rem;
  transition: all 0.3s ease;
  backdrop-filter: blur(10px);

  &:hover {
    background: rgba(0, 0, 0, 0.8);
    transform: scale(1.1);
  }

  @media (max-width: 768px) {
    width: 36px;
    height: 36px;
    font-size: 1.1rem;
  }

  @media (max-width: 480px) {
    width: 40px;
    height: 40px;
    font-size: 1.2rem;
  }
`;

const MenuDropdown = styled(motion.div)`
  position: absolute;
  top: 100%;
  right: 0;
  background: rgba(0, 0, 0, 0.9);
  backdrop-filter: blur(15px);
  border-radius: 10px;
  padding: 8px 0;
  min-width: 120px;
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.3);
  border: 1px solid rgba(255, 255, 255, 0.1);
  z-index: 10;

  @media (max-width: 480px) {
    min-width: 140px;
  }
`;

const MenuItem = styled.button`
  background: none;
  border: none;
  color: white;
  padding: 10px 16px;
  width: 100%;
  text-align: left;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 8px;
  font-size: 0.9rem;
  transition: all 0.2s ease;

  &:hover {
    background: rgba(255, 255, 255, 0.1);
  }



  @media (max-width: 480px) {
    padding: 12px 18px;
    font-size: 1rem;
  }
`;

const FooterMessage = styled.div`
  position: fixed;
  left: 0;
  right: 0;
  bottom: 0;
  background: none;
  color: #fff;
  font-family: 'Pacifico', cursive;
  font-size: 1.1rem;
  text-align: center;
  padding: 10px 0 14px 0;
  z-index: 9999;
  letter-spacing: 1px;
  text-shadow: 0 2px 8px rgba(0,0,0,0.2);
  pointer-events: none;
  user-select: none;

  @media (max-width: 768px) {
    font-size: 1rem;
    padding: 8px 0 12px 0;
  }

  @media (max-width: 480px) {
    font-size: 0.9rem;
    padding: 6px 0 10px 0;
  }
`;

const Gallery = () => {
  const navigate = useNavigate();
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedImage, setSelectedImage] = useState(null);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);
  const [audio] = useState(new Audio('/romantic-music.mp3'));
  const [isRefreshing, setIsRefreshing] = useState(false);
  const [musicFiles, setMusicFiles] = useState([]);
  const [currentMusic, setCurrentMusic] = useState(null);
  const [deleting, setDeleting] = useState(false);
  const [openMenu, setOpenMenu] = useState(null);

  useEffect(() => {
    audio.loop = true;
    audio.volume = 0.2;

    fetchImages();
    fetchMusicFiles();

    return () => {
      audio.pause();
      audio.currentTime = 0;
    };
  }, [audio]);

  // Auto-refresh when coming from upload page
  useEffect(() => {
    const isFromUpload = sessionStorage.getItem('fromUpload');
    if (isFromUpload) {
      sessionStorage.removeItem('fromUpload');
      fetchImages();
      fetchMusicFiles();
    }
  }, []);

  const fetchImages = async () => {
    try {
      setIsRefreshing(true);
      const response = await axios.get('http://localhost:8080/api/images');
      setImages(response.data);
    } catch (error) {
      console.error('Error fetching images:', error);
    } finally {
      setLoading(false);
      setIsRefreshing(false);
    }
  };

  const fetchMusicFiles = async () => {
    try {
      const response = await axios.get('http://localhost:8080/api/music');
      setMusicFiles(response.data);
      // Set the first music file as current if available
      if (response.data.length > 0 && !currentMusic) {
        setCurrentMusic(response.data[0]);
      }
    } catch (error) {
      console.error('Error fetching music files:', error);
    }
  };

  const toggleMusic = () => {
    if (isPlaying) {
      audio.pause();
    } else {
      if (currentMusic) {
        audio.src = `http://localhost:8080/api/images/${currentMusic}`;
        audio.play().catch(() => {
          console.log('Audio autoplay blocked');
        });
      } else {
        audio.play().catch(() => {
          console.log('Audio autoplay blocked');
        });
      }
    }
    setIsPlaying(!isPlaying);
  };

  const openLightbox = (image, index) => {
    setSelectedImage(image);
    setCurrentIndex(index);
  };

  const closeLightbox = () => {
    setSelectedImage(null);
  };

  const nextImage = () => {
    setCurrentIndex((prev) => (prev + 1) % images.length);
  };

  const prevImage = () => {
    setCurrentIndex((prev) => (prev - 1 + images.length) % images.length);
  };

  const handleKeyPress = (e) => {
    if (selectedImage) {
      if (e.key === 'Escape') closeLightbox();
      if (e.key === 'ArrowRight') nextImage();
      if (e.key === 'ArrowLeft') prevImage();
    }
  };

  useEffect(() => {
    document.addEventListener('keydown', handleKeyPress);
    return () => document.removeEventListener('keydown', handleKeyPress);
  }, [selectedImage]);

  // Close menu when clicking outside
  useEffect(() => {
    const handleClickOutside = () => {
      setOpenMenu(null);
    };

    if (openMenu) {
      document.addEventListener('click', handleClickOutside);
      return () => document.removeEventListener('click', handleClickOutside);
    }
  }, [openMenu]);

  const handleDeletePhoto = async (image) => {
    if (!window.confirm('Are you sure you want to delete this photo?')) return;
    setDeleting(true);
    setOpenMenu(null); // Close menu after action
    try {
      await axios.delete(`http://localhost:8080/api/images/${encodeURIComponent(image)}`);
      setImages((prev) => prev.filter((img) => img !== image));
    } catch (error) {
      alert('Failed to delete photo.');
    } finally {
      setDeleting(false);
    }
  };

  const toggleMenu = (imageName, e) => {
    e.stopPropagation();
    setOpenMenu(openMenu === imageName ? null : imageName);
  };

  const closeMenu = () => {
    setOpenMenu(null);
  };

  if (loading) {
    return (
      <GalleryContainer>
        <LoadingContainer>
          <motion.div
            animate={{ rotate: 360 }}
            transition={{ duration: 2, repeat: Infinity, ease: "linear" }}
          >
            ❤️
          </motion.div>
          <span style={{ marginLeft: '10px' }}>Loading our memories...</span>
        </LoadingContainer>
        <FooterMessage>
          Made with ❤️ by the one who loves you the most
        </FooterMessage>
      </GalleryContainer>
    );
  }

  if (!images.length) return <div style={{textAlign:'center',marginTop:'40px'}}>No photos yet.</div>;

  return (
    <>
      <GalleryContainer>
        <Header
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          <Title>
            <FaHeart /> Our Photo Gallery
          </Title>
          <HeaderControls>
            {musicFiles.length > 0 && (
              <MusicSelector
                value={currentMusic || ''}
                onChange={(e) => {
                  setCurrentMusic(e.target.value);
                  if (isPlaying) {
                    audio.pause();
                    setIsPlaying(false);
                  }
                }}
              >
                {musicFiles.map(music => (
                  <option key={music} value={music}>
                    {music.replace(/\.(mp3|wav|m4a)$/i, '')}
                  </option>
                ))}
              </MusicSelector>
            )}
            <MusicControl
              onClick={toggleMusic}
            >
              {isPlaying ? <FaPause /> : <FaPlay />}
            </MusicControl>
            <NavButton
              onClick={() => {
                fetchImages();
                fetchMusicFiles();
              }}
              disabled={isRefreshing}
            >
              <FaSync style={{ animation: isRefreshing ? 'spin 1s linear infinite' : 'none' }} /> 
              {isRefreshing ? 'Refreshing...' : 'Refresh'}
            </NavButton>
            <NavButton
              onClick={() => navigate('/')}
            >
              <FaHome /> Home
            </NavButton>
          </HeaderControls>
        </Header>

        <GalleryGrid>
          {images.map((image, idx) => (
            <ImageCard key={image}>
              <div style={{ position: 'relative' }}>
                <Image src={`/api/images/${encodeURIComponent(image)}`} alt={`Memory #${idx+1}`} />
                <ImageOverlay>Memory #{idx+1}</ImageOverlay>
              </div>
              <PhotoInteractions photoName={image} onRefresh={() => {
                fetchImages();
                fetchMusicFiles();
              }} />
            </ImageCard>
          ))}
        </GalleryGrid>

        <AnimatePresence>
          {selectedImage && (
            <Lightbox
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              onClick={closeLightbox}
            >
              <LightboxControls>
                <LightboxButton onClick={closeLightbox}>
                  <FaTimes />
                </LightboxButton>
              </LightboxControls>

              <NavigationButtons>
                <NavButtonLightbox onClick={prevImage}>
                  <FaChevronLeft />
                </NavButtonLightbox>
                <NavButtonLightbox onClick={nextImage}>
                  <FaChevronRight />
                </NavButtonLightbox>
              </NavigationButtons>

              <div style={{ position: 'relative', maxWidth: '90%', maxHeight: '90%' }}>
                <LightboxImage
                  src={`http://localhost:8080/api/images/${images[currentIndex]}`}
                  alt={`Memory ${currentIndex + 1}`}
                  onClick={(e) => e.stopPropagation()}
                />
                <PhotoActionsMenu style={{ position: 'absolute', top: '20px', right: '20px' }}>
                  <MenuButton
                    onClick={(e) => toggleMenu(images[currentIndex], e)}
                    disabled={deleting}
                    title="Photo actions"
                  >
                    <FaEllipsisV />
                  </MenuButton>
                  <AnimatePresence>
                    {openMenu === images[currentIndex] && (
                      <MenuDropdown
                        initial={{ opacity: 0, y: -10, scale: 0.95 }}
                        animate={{ opacity: 1, y: 0, scale: 1 }}
                        exit={{ opacity: 0, y: -10, scale: 0.95 }}
                        transition={{ duration: 0.2 }}
                      >
                        <MenuItem 
                          onClick={(e) => { e.stopPropagation(); handleDeletePhoto(images[currentIndex]); }} 
                          disabled={deleting}
                        >
                          Delete
                        </MenuItem>
                      </MenuDropdown>
                    )}
                  </AnimatePresence>
                </PhotoActionsMenu>
                <PhotoInteractions photoName={images[currentIndex]} show={true} />
              </div>
            </Lightbox>
          )}
        </AnimatePresence>
      </GalleryContainer>
      <FooterMessage>
        Made with ❤️ by the one who loves you the most
      </FooterMessage>
    </>
  );
};

export default Gallery;
