import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import { FaHeart, FaTimes, FaChevronLeft, FaChevronRight, FaHome, FaPlay, FaPause, FaSync, FaMusic, FaTrash, FaEllipsisV, FaUser } from 'react-icons/fa';
import axios from 'axios';
import PhotoInteractions from './PhotoInteractions';
import { useAudio } from '../contexts/AudioContext';

const GalleryContainer = styled.div`
  min-height: 100vh;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  padding: 20px;
  padding-top: 20px; /* Add top padding for header */
  padding-bottom: 100px; /* Increased space for footer */
  position: relative;

  @media (max-width: 768px) {
    padding: 15px 10px 90px 10px;
    padding-top: 15px;
  }

  @media (max-width: 480px) {
    padding: 10px 8px 80px 8px;
    padding-top: 10px;
  }
`;

const Header = styled(motion.div)`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 30px;
  padding: 20px;
  background: transparent;
  flex-wrap: wrap;
  gap: 15px;
  position: fixed;
  top: 20px;
  left: 20px;
  right: 20px;
  z-index: 1000;

  @media (max-width: 768px) {
    flex-direction: column;
    align-items: stretch;
    gap: 16px;
    padding: 16px 12px;
    margin-bottom: 20px;
    top: 15px;
    left: 15px;
    right: 15px;
  }

  @media (max-width: 480px) {
    padding: 12px 8px;
    gap: 12px;
    margin-bottom: 15px;
    top: 10px;
    left: 10px;
    right: 10px;
  }
`;

const Title = styled.h1`
  color: white;
  font-family: 'Pacifico', cursive;
  font-size: 2.2rem;
  font-weight: 400;
  display: flex;
  align-items: center;
  gap: 10px;
  letter-spacing: 1px;
  line-height: 1.1;
  margin: 0;

  @media (max-width: 768px) {
    font-size: 1.8rem;
    justify-content: center;
    text-align: center;
    width: 100%;
  }

  @media (max-width: 480px) {
    font-size: 1.4rem;
    gap: 6px;
  }

  @media (max-width: 360px) {
    font-size: 1.2rem;
  }
`;

const HeaderControls = styled.div`
  display: flex;
  gap: 10px;
  align-items: center;
  flex-wrap: wrap;

  @media (max-width: 768px) {
    justify-content: center;
    gap: 8px;
  }

  @media (max-width: 480px) {
    flex-direction: column;
    width: 100%;
    gap: 10px;
  }
`;

const NavButton = styled(motion.button)`
  background: rgba(255, 255, 255, 0.2);
  border: none;
  color: white;
  padding: 10px 20px;
  border-radius: 25px;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 8px;
  font-weight: 500;
  transition: all 0.3s ease;
  white-space: nowrap;

  &:hover {
    background: rgba(255, 255, 255, 0.3);
    transform: translateY(-2px);
  }

  &:disabled {
    opacity: 0.6;
    cursor: not-allowed;
  }

  @media (max-width: 768px) {
    padding: 8px 16px;
    font-size: 0.9rem;
  }

  @media (max-width: 480px) {
    width: 100%;
    justify-content: center;
    padding: 12px 16px;
    font-size: 1rem;
  }
`;

const MusicControl = styled(motion.button)`
  background: rgba(255, 255, 255, 0.2);
  border: none;
  color: white;
  width: 44px;
  height: 44px;
  border-radius: 50%;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.3s ease;

  &:hover {
    background: rgba(255, 255, 255, 0.3);
    transform: scale(1.1);
  }

  @media (max-width: 480px) {
    width: 48px;
    height: 48px;
    font-size: 1.1rem;
  }
`;

const UserInfo = styled.div`
  display: flex;
  align-items: center;
  gap: 12px;
  background: rgba(255, 255, 255, 0.15);
  padding: 8px 16px;
  border-radius: 25px;
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.2);

  @media (max-width: 768px) {
    flex-direction: column;
    gap: 8px;
    padding: 12px;
  }
`;

const UserAvatar = styled.div`
  width: 36px;
  height: 36px;
  border-radius: 50%;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
  display: flex;
  align-items: center;
  justify-content: center;
  font-weight: 600;
  font-size: 1rem;

  @media (max-width: 768px) {
    width: 32px;
    height: 32px;
    font-size: 0.9rem;
  }
`;

const UserDetails = styled.div`
  display: flex;
  flex-direction: column;

  @media (max-width: 768px) {
    align-items: center;
    text-align: center;
  }
`;

const UserName = styled.span`
  color: white;
  font-weight: 600;
  font-size: 0.9rem;

  @media (max-width: 768px) {
    font-size: 0.8rem;
  }
`;

const UserEmail = styled.span`
  color: rgba(255, 255, 255, 0.8);
  font-size: 0.8rem;

  @media (max-width: 768px) {
    font-size: 0.7rem;
  }
`;

const LogoutButton = styled.button`
  background: rgba(231, 76, 60, 0.8);
  border: none;
  color: white;
  padding: 6px 12px;
  border-radius: 15px;
  cursor: pointer;
  font-size: 0.8rem;
  font-weight: 500;
  transition: all 0.3s ease;

  &:hover {
    background: #e74c3c;
    transform: translateY(-1px);
  }

  @media (max-width: 768px) {
    padding: 8px 16px;
    font-size: 0.9rem;
  }
`;

const PersonIcon = styled(motion.button)`
  background: ${props => props.profilePicture ? 'none' : 'rgba(255, 255, 255, 0.2)'};
  border: none;
  color: white;
  width: 44px;
  height: 44px;
  border-radius: 50%;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.3s ease;
  position: relative;
  overflow: hidden;

  ${props => props.profilePicture && `
    background-image: url(${props.profilePicture});
    background-size: cover;
    background-position: center;
    background-repeat: no-repeat;
  `}

  &:hover {
    background: ${props => props.profilePicture ? 'rgba(255, 255, 255, 0.1)' : 'rgba(255, 255, 255, 0.3)'};
    transform: scale(1.1);
  }

  @media (max-width: 480px) {
    width: 48px;
    height: 48px;
    font-size: 1.1rem;
  }
`;

const PersonDropdown = styled(motion.div)`
  position: absolute;
  top: 100%;
  right: 0;
  margin-top: 8px;
  background: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(10px);
  border-radius: 12px;
  box-shadow: 0 8px 25px rgba(0, 0, 0, 0.15);
  overflow: hidden;
  min-width: 150px;
  z-index: 1001;

  @media (max-width: 480px) {
    min-width: 140px;
  }
`;

const DropdownItem = styled.button`
  width: 100%;
  padding: 12px 16px;
  border: none;
  background: transparent;
  color: #333;
  font-size: 0.9rem;
  font-weight: 500;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 10px;
  transition: all 0.2s ease;

  &:hover {
    background: rgba(102, 126, 234, 0.1);
    color: #667eea;
  }

  &:first-child {
    border-bottom: 1px solid rgba(0, 0, 0, 0.1);
  }

  @media (max-width: 480px) {
    padding: 14px 16px;
    font-size: 1rem;
  }
`;

const UserInfoContainer = styled.div`
  position: relative;
`;

const WelcomeMessage = styled.div`
  color: white;
  font-size: 0.9rem;
  font-weight: 500;
  text-align: center;
  text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.3);

  @media (max-width: 768px) {
    font-size: 0.8rem;
  }

  @media (max-width: 480px) {
    font-size: 0.75rem;
  }
`;

const GalleryGrid = styled(motion.div)`
  display: flex;
  flex-direction: column;
  gap: 25px;
  max-width: 600px;
  margin: 0 auto;
  margin-top: 120px; /* Space for fixed header */
  width: 100%;

  @media (max-width: 768px) {
    gap: 20px;
    max-width: 500px;
    margin-top: 140px; /* More space for stacked header on mobile */
  }

  @media (max-width: 480px) {
    gap: 18px;
    max-width: 100%;
    margin-top: 160px; /* Even more space for mobile */
  }
`;

const ImageCard = styled(motion.div)`
  position: relative;
  border-radius: 15px;
  overflow: visible;
  cursor: pointer;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.2);
  transition: all 0.3s ease;
  width: 100%;

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.3);
  }

  @media (max-width: 480px) {
    &:hover {
      transform: translateY(-2px);
    }
  }
`;

const Image = styled.img`
  width: 100%;
  height: 400px;
  object-fit: cover;
  transition: transform 0.3s ease;
  border-radius: 15px;

  ${ImageCard}:hover & {
    transform: scale(1.02);
  }

  @media (max-width: 768px) {
    height: 350px;
  }

  @media (max-width: 480px) {
    height: 300px;

    ${ImageCard}:hover & {
      transform: scale(1.01);
    }
  }
`;

const ImageOverlay = styled.div`
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  background: linear-gradient(transparent, rgba(0, 0, 0, 0.7));
  padding: 20px;
  color: white;
  transform: translateY(100%);
  transition: transform 0.3s ease;

  ${ImageCard}:hover & {
    transform: translateY(0);
  }
`;

const LoadingContainer = styled.div`
  display: flex;
  justify-content: center;
