const Gallery = ({ currentUser, onLogout }) => {
  const navigate = useNavigate();
  const { isPlaying, toggleMusic } = useAudio();
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedImage, setSelectedImage] = useState(null);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isRefreshing, setIsRefreshing] = useState(false);
  const [deleting, setDeleting] = useState(false);
  const [openMenu, setOpenMenu] = useState(null);
  const [deleteSuccess, setDeleteSuccess] = useState(false);
  const [showPersonDropdown, setShowPersonDropdown] = useState(false);

  useEffect(() => {
    fetchImages();
  }, []);

  // Auto-refresh when coming from upload page
  useEffect(() => {
    const isFromUpload = sessionStorage.getItem('fromUpload');
    if (isFromUpload) {
      sessionStorage.removeItem('fromUpload');
      fetchImages();
    }
  }, []);

  const fetchImages = async () => {
    try {
      setIsRefreshing(true);
      const response = await axios.get('http://localhost:8080/api/images');
      setImages(response.data);
    } catch (error) {
      console.error('Error fetching images:', error);
    } finally {
      setLoading(false);
      setIsRefreshing(false);
    }
  };



  const openLightbox = (image, index) => {
    setSelectedImage(image);
    setCurrentIndex(index);
  };

  const closeLightbox = () => {
    setSelectedImage(null);
  };

  const nextImage = () => {
    setCurrentIndex((prev) => (prev + 1) % images.length);
  };

  const prevImage = () => {
    setCurrentIndex((prev) => (prev - 1 + images.length) % images.length);
  };

  const handleKeyPress = (e) => {
    if (selectedImage) {
      if (e.key === 'Escape') closeLightbox();
      if (e.key === 'ArrowRight') nextImage();
      if (e.key === 'ArrowLeft') prevImage();
    }
  };

  useEffect(() => {
    document.addEventListener('keydown', handleKeyPress);
    return () => document.removeEventListener('keydown', handleKeyPress);
  }, [selectedImage]);

  // Close menu when clicking outside
  useEffect(() => {
    const handleClickOutside = () => {
      setOpenMenu(null);
      setShowPersonDropdown(false);
    };

    if (openMenu || showPersonDropdown) {
      document.addEventListener('click', handleClickOutside);
      return () => document.removeEventListener('click', handleClickOutside);
    }
  }, [openMenu, showPersonDropdown]);

  const handleDeletePhoto = async (image) => {
    if (!window.confirm('Are you sure you want to delete this photo?')) return;
    setDeleting(true);
    setOpenMenu(null); // Close menu after action
    try {
      await axios.delete(`http://localhost:8080/api/images/${encodeURIComponent(image)}`);
      setImages((prev) => prev.filter((img) => img !== image));
      // Close lightbox if the deleted image was selected
      if (selectedImage === image) {
        closeLightbox();
      }
      setDeleteSuccess(true);
      setTimeout(() => setDeleteSuccess(false), 3000);
    } catch (error) {
      console.error('Error deleting photo:', error);
      alert(`Failed to delete photo: ${error.response?.data || error.message}`);
    } finally {
      setDeleting(false);
    }
  };

  const toggleMenu = (imageName, e) => {
    e.stopPropagation();
    setOpenMenu(openMenu === imageName ? null : imageName);
  };

  const closeMenu = () => {
    setOpenMenu(null);
  };

  const togglePersonDropdown = (e) => {
    e.stopPropagation();
    setShowPersonDropdown(!showPersonDropdown);
  };

  const handleHomeClick = () => {
    setShowPersonDropdown(false);
    fetchImages();
    navigate('/');
  };

  const handleLogoutClick = () => {
    setShowPersonDropdown(false);
    onLogout();
  };

  if (loading) {
    return (
      <GalleryContainer>
        <LoadingContainer>
          <motion.div
            animate={{ rotate: 360 }}
            transition={{ duration: 2, repeat: Infinity, ease: "linear" }}
          >

          </motion.div>
          <span style={{ marginLeft: '10px' }}>Loading our memories...</span>
        </LoadingContainer>
        <FooterMessage>
          Made with  by the one who loves you the most
        </FooterMessage>
      </GalleryContainer>
    );
  }

  return (
    <>
      <GalleryContainer>
        <Header
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          <Title>
            <FaHeart /> Our Photo Gallery
          </Title>
          <HeaderControls>
            <MusicControl
              onClick={toggleMusic}
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.9 }}
              title={isPlaying ? 'Pause Music' : 'Play Music'}
            >
              {isPlaying ? <FaPause /> : <FaPlay />}
            </MusicControl>
            {currentUser && (
              <UserInfoContainer>
                <PersonIcon
                  onClick={togglePersonDropdown}
                  whileHover={{ scale: 1.1 }}
                  whileTap={{ scale: 0.9 }}
                  title="User menu"
                  profilePicture={currentUser.profilePicture ? `http://localhost:8080/api/auth/profile-pictures/${currentUser.profilePicture}` : null}
                >
                  {!currentUser.profilePicture && <FaUser />}
                </PersonIcon>
                <AnimatePresence>
                  {showPersonDropdown && (
                    <PersonDropdown
                      initial={{ opacity: 0, y: -10, scale: 0.95 }}
                      animate={{ opacity: 1, y: 0, scale: 1 }}
                      exit={{ opacity: 0, y: -10, scale: 0.95 }}
                      transition={{ duration: 0.2 }}
                    >
                      <DropdownItem onClick={handleHomeClick}>
                        <FaHome />
                        Home
                      </DropdownItem>
                      <DropdownItem onClick={handleLogoutClick}>
                        Logout
                      </DropdownItem>
                    </PersonDropdown>
                  )}
                </AnimatePresence>
              </UserInfoContainer>
            )}
          </HeaderControls>
        </Header>

        <GalleryGrid
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5, delay: 0.2 }}
        >
          {images.map((image, index) => (
            <ImageCard
              key={image}
              layoutId={`image-${index}`}
              whileHover={{ scale: 1.02 }}
              transition={{ duration: 0.2 }}
            >
              <div style={{ position: 'relative', overflow: 'hidden', borderRadius: '15px' }}>
                <Image
                  src={`http://localhost:8080/api/images/${image}`}
                  alt={`Memory ${index + 1}`}
                  loading="lazy"
                  onClick={() => openLightbox(image, index)}
                  style={{ cursor: 'pointer' }}
                />
                <PhotoActionsMenu>
                  <MenuButton
                    onClick={(e) => toggleMenu(image, e)}
                    disabled={deleting}
                    title="Photo actions"
                  >
                    <FaEllipsisV />
                  </MenuButton>
                  <AnimatePresence>
                    {openMenu === image && (
                      <MenuDropdown
                        initial={{ opacity: 0, y: -10, scale: 0.95 }}
                        animate={{ opacity: 1, y: 0, scale: 1 }}
                        exit={{ opacity: 0, y: -10, scale: 0.95 }}
                        transition={{ duration: 0.2 }}
                      >
                        <MenuItem 
                          onClick={(e) => { e.stopPropagation(); handleDeletePhoto(image); }} 
                          disabled={deleting}
                        >
                          Delete
                        </MenuItem>
                      </MenuDropdown>
                    )}
                  </AnimatePresence>
                </PhotoActionsMenu>
                <ImageOverlay>
                  <div>Memory #{index + 1}</div>
                </ImageOverlay>
                <PhotoInteractions photoName={image} show={true} />
              </div>
            </ImageCard>
          ))}
        </GalleryGrid>

        {deleteSuccess && (
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 20 }}
            style={{
              position: 'fixed',
              top: '20px',
              right: '20px',
              background: '#27ae60',
              color: 'white',
              padding: '12px 20px',
              borderRadius: '10px',
              zIndex: 1000,
              boxShadow: '0 4px 12px rgba(0,0,0,0.15)'
            }}
          >
             Photo deleted successfully!
          </motion.div>
        )}

        <AnimatePresence>
          {selectedImage && (
            <Lightbox
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              onClick={closeLightbox}
            >
              <LightboxControls>
                <LightboxButton onClick={closeLightbox}>
                  <FaTimes />
                </LightboxButton>
              </LightboxControls>

              <NavigationButtons>
                <NavButtonLightbox onClick={prevImage}>
                  <FaChevronLeft />
                </NavButtonLightbox>
                <NavButtonLightbox onClick={nextImage}>
                  <FaChevronRight />
                </NavButtonLightbox>
              </NavigationButtons>

              <div style={{ position: 'relative', maxWidth: '90%', maxHeight: '90%' }}>
                <LightboxImage
                  src={`http://localhost:8080/api/images/${images[currentIndex]}`}
                  alt={`Memory ${currentIndex + 1}`}
                  onClick={(e) => e.stopPropagation()}
                />
                <PhotoActionsMenu style={{ position: 'absolute', top: '20px', right: '20px' }}>
                  <MenuButton
                    onClick={(e) => toggleMenu(images[currentIndex], e)}
                    disabled={deleting}
                    title="Photo actions"
                  >
                    <FaEllipsisV />
                  </MenuButton>
                  <AnimatePresence>
                    {openMenu === images[currentIndex] && (
                      <MenuDropdown
                        initial={{ opacity: 0, y: -10, scale: 0.95 }}
                        animate={{ opacity: 1, y: 0, scale: 1 }}
                        exit={{ opacity: 0, y: -10, scale: 0.95 }}
                        transition={{ duration: 0.2 }}
                      >
                        <MenuItem 
                          onClick={(e) => { e.stopPropagation(); handleDeletePhoto(images[currentIndex]); }} 
                          disabled={deleting}
                        >
                          Delete
                        </MenuItem>
                      </MenuDropdown>
                    )}
                  </AnimatePresence>
                </PhotoActionsMenu>
                <PhotoInteractions photoName={images[currentIndex]} show={true} />
              </div>
            </Lightbox>
          )}
        </AnimatePresence>
      </GalleryContainer>
      <FooterMessage>
        Made with  by the one who loves you the most
      </FooterMessage>
    </>
  );
};

export default Gallery;
