import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { FaHeart, FaComment, FaTimes, FaPaperPlane } from 'react-icons/fa';
import axios from 'axios';

const InteractionsContainer = styled.div`
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  background: linear-gradient(transparent, rgba(0, 0, 0, 0.8));
  padding: 20px;
  color: white;
  transform: translateY(100%);
  transition: transform 0.3s ease;

  ${({ show }) => show && `
    transform: translateY(0);
  `}
`;

const InteractionButtons = styled.div`
  display: flex;
  gap: 15px;
  margin-bottom: 15px;
`;

const InteractionButton = styled(motion.button)`
  background: rgba(255, 255, 255, 0.2);
  border: none;
  color: white;
  padding: 8px 12px;
  border-radius: 20px;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 5px;
  font-size: 0.9rem;
  backdrop-filter: blur(10px);
  transition: all 0.3s ease;

  &:hover {
    background: rgba(255, 255, 255, 0.3);
  }

  &.liked {
    background: #e74c3c;
    color: white;
  }
`;

const LikeCount = styled.span`
  font-size: 0.8rem;
  opacity: 0.8;
`;

const CommentsSection = styled.div`
  max-height: 200px;
  overflow-y: auto;
  margin-bottom: 15px;
`;

const CommentItem = styled.div`
  background: rgba(255, 255, 255, 0.1);
  padding: 10px;
  border-radius: 10px;
  margin-bottom: 8px;
  backdrop-filter: blur(10px);
`;

const CommentAuthor = styled.div`
  font-weight: 600;
  font-size: 0.8rem;
  margin-bottom: 4px;
  color: #ffd700;
`;

const CommentText = styled.div`
  font-size: 0.9rem;
  line-height: 1.4;
`;

const CommentTime = styled.div`
  font-size: 0.7rem;
  opacity: 0.6;
  margin-top: 4px;
`;

const CommentForm = styled.form`
  display: flex;
  gap: 10px;
  align-items: center;
`;

const CommentInput = styled.input`
  flex: 1;
  background: rgba(255, 255, 255, 0.2);
  border: 1px solid rgba(255, 255, 255, 0.3);
  color: white;
  padding: 8px 12px;
  border-radius: 20px;
  font-size: 0.9rem;
  backdrop-filter: blur(10px);

  &::placeholder {
    color: rgba(255, 255, 255, 0.7);
  }

  &:focus {
    outline: none;
    border-color: rgba(255, 255, 255, 0.5);
  }
`;

const SendButton = styled(motion.button)`
  background: #e74c3c;
  border: none;
  color: white;
  width: 40px;
  height: 40px;
  border-radius: 50%;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.3s ease;
  font-size: 1rem;

  &:hover {
    background: #c0392b;
    transform: scale(1.1);
  }

  &:disabled {
    opacity: 0.5;
    cursor: not-allowed;
    transform: none;
  }
`;

const AuthorInput = styled.input`
  background: rgba(255, 255, 255, 0.2);
  border: 1px solid rgba(255, 255, 255, 0.3);
  color: white;
  padding: 6px 10px;
  border-radius: 15px;
  font-size: 0.8rem;
  backdrop-filter: blur(10px);
  width: 120px;

  &::placeholder {
    color: rgba(255, 255, 255, 0.7);
  }

  &:focus {
    outline: none;
    border-color: rgba(255, 255, 255, 0.5);
  }
`;

const PhotoInteractions = ({ photoName, show }) => {
  const [liked, setLiked] = useState(false);
  const [likeCount, setLikeCount] = useState(0);
  const [comments, setComments] = useState([]);
  const [showComments, setShowComments] = useState(false);
  const [newComment, setNewComment] = useState('');
  const [authorName, setAuthorName] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Default user name (you can make this configurable)
  const defaultUserName = 'Lover';

  useEffect(() => {
    if (photoName) {
      fetchLikeStatus();
      fetchLikeCount();
      fetchComments();
    }
  }, [photoName]);

  const fetchLikeStatus = async () => {
    try {
      const response = await axios.get(`http://localhost:8080/api/likes/${photoName}/user/${defaultUserName}`);
      setLiked(response.data);
    } catch (error) {
      console.error('Error fetching like status:', error);
    }
  };

  const fetchLikeCount = async () => {
    try {
      const response = await axios.get(`http://localhost:8080/api/likes/${photoName}`);
      setLikeCount(response.data);
    } catch (error) {
      console.error('Error fetching like count:', error);
    }
  };

  const fetchComments = async () => {
    try {
      const response = await axios.get(`http://localhost:8080/api/comments/${photoName}`);
      setComments(response.data);
    } catch (error) {
      console.error('Error fetching comments:', error);
    }
  };

  const handleLike = async () => {
    try {
      const response = await axios.post('http://localhost:8080/api/likes', {
        photoName: photoName,
        userName: defaultUserName
      });
      setLiked(response.data.liked);
      setLikeCount(response.data.likeCount);
    } catch (error) {
      console.error('Error toggling like:', error);
    }
  };

  const handleComment = async (e) => {
    e.preventDefault();
    if (!newComment.trim() || !authorName.trim()) return;

    setIsSubmitting(true);
    try {
      const response = await axios.post('http://localhost:8080/api/comments', {
        photoName: photoName,
        commentText: newComment.trim(),
        authorName: authorName.trim()
      });

      setComments([response.data, ...comments]);
      setNewComment('');
      setIsSubmitting(false);
    } catch (error) {
      console.error('Error adding comment:', error);
      setIsSubmitting(false);
    }
  };

  const formatTime = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString() + ' ' + date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  return (
    <InteractionsContainer show={show}>
      <InteractionButtons>
        <InteractionButton
          onClick={handleLike}
          className={liked ? 'liked' : ''}
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
        >
          <FaHeart style={{ color: liked ? '#fff' : '#e74c3c' }} />
          <span>Like</span>
          {likeCount > 0 && <LikeCount>({likeCount})</LikeCount>}
        </InteractionButton>

        <InteractionButton
          onClick={() => setShowComments(!showComments)}
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
        >
          <FaComment />
          <span>Comment</span>
          {comments.length > 0 && <LikeCount>({comments.length})</LikeCount>}
        </InteractionButton>
      </InteractionButtons>

      {showComments && (
        <motion.div
          initial={{ opacity: 0, height: 0 }}
          animate={{ opacity: 1, height: 'auto' }}
          exit={{ opacity: 0, height: 0 }}
          transition={{ duration: 0.3 }}
        >
          <CommentsSection>
            {comments.map((comment) => (
              <CommentItem key={comment.id}>
                <CommentAuthor>{comment.authorName}</CommentAuthor>
                <CommentText>{comment.commentText}</CommentText>
                <CommentTime>{formatTime(comment.createdAt)}</CommentTime>
              </CommentItem>
            ))}
            {comments.length === 0 && (
              <div style={{ textAlign: 'center', opacity: 0.7, fontStyle: 'italic' }}>
                No comments yet. Be the first to comment! 💕
              </div>
            )}
          </CommentsSection>

          <CommentForm onSubmit={handleComment}>
            <AuthorInput
              type="text"
              placeholder="Your name"
              value={authorName}
              onChange={(e) => setAuthorName(e.target.value)}
              required
            />
            <CommentInput
              type="text"
              placeholder="Write a romantic comment..."
              value={newComment}
              onChange={(e) => setNewComment(e.target.value)}
              required
            />
            <SendButton
              type="submit"
              disabled={isSubmitting || !newComment.trim() || !authorName.trim()}
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.9 }}
            >
              <FaPaperPlane />
            </SendButton>
          </CommentForm>
        </motion.div>
      )}
    </InteractionsContainer>
  );
};

export default PhotoInteractions;
