const Gallery = () => {
  const navigate = useNavigate();
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedImage, setSelectedImage] = useState(null);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);
  const [audio] = useState(new Audio('/romantic-music.mp3'));
  const [isRefreshing, setIsRefreshing] = useState(false);
  const [musicFiles, setMusicFiles] = useState([]);
  const [currentMusic, setCurrentMusic] = useState(null);

  useEffect(() => {
    audio.loop = true;
    audio.volume = 0.2;

    fetchImages();
    fetchMusicFiles();

    return () => {
      audio.pause();
      audio.currentTime = 0;
    };
  }, [audio]);

  // Auto-refresh when coming from upload page
  useEffect(() => {
    const isFromUpload = sessionStorage.getItem('fromUpload');
    if (isFromUpload) {
      sessionStorage.removeItem('fromUpload');
      fetchImages();
      fetchMusicFiles();
    }
  }, []);

  const fetchImages = async () => {
    try {
      setIsRefreshing(true);
      const response = await axios.get('http://localhost:8080/api/images');
      setImages(response.data);
    } catch (error) {
      console.error('Error fetching images:', error);
    } finally {
      setLoading(false);
      setIsRefreshing(false);
    }
  };

  const fetchMusicFiles = async () => {
    try {
      const response = await axios.get('http://localhost:8080/api/music');
      setMusicFiles(response.data);
      // Set the first music file as current if available
      if (response.data.length > 0 && !currentMusic) {
        setCurrentMusic(response.data[0]);
      }
    } catch (error) {
      console.error('Error fetching music files:', error);
    }
  };

  const toggleMusic = () => {
    if (isPlaying) {
      audio.pause();
    } else {
      if (currentMusic) {
        audio.src = `http://localhost:8080/api/images/${currentMusic}`;
        audio.play().catch(() => {
          console.log('Audio autoplay blocked');
        });
      } else {
        audio.play().catch(() => {
          console.log('Audio autoplay blocked');
        });
      }
    }
    setIsPlaying(!isPlaying);
  };

  const openLightbox = (image, index) => {
    setSelectedImage(image);
    setCurrentIndex(index);
  };

  const closeLightbox = () => {
    setSelectedImage(null);
  };

  const nextImage = () => {
    setCurrentIndex((prev) => (prev + 1) % images.length);
  };

  const prevImage = () => {
    setCurrentIndex((prev) => (prev - 1 + images.length) % images.length);
  };

  const handleKeyPress = (e) => {
    if (selectedImage) {
      if (e.key === 'Escape') closeLightbox();
      if (e.key === 'ArrowRight') nextImage();
      if (e.key === 'ArrowLeft') prevImage();
    }
  };

  useEffect(() => {
    document.addEventListener('keydown', handleKeyPress);
    return () => document.removeEventListener('keydown', handleKeyPress);
  }, [selectedImage]);

  if (loading) {
    return (
      <GalleryContainer>
        <LoadingContainer>
          <motion.div
            animate={{ rotate: 360 }}
            transition={{ duration: 2, repeat: Infinity, ease: "linear" }}
          >
            ❤️
          </motion.div>
          <span style={{ marginLeft: '10px' }}>Loading our memories...</span>
        </LoadingContainer>
      </GalleryContainer>
    );
  }

  return (
    <GalleryContainer>
      <Header
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
      >
        <Title>
          <FaHeart /> Our Photo Gallery
        </Title>
        <div style={{ display: 'flex', gap: '10px', alignItems: 'center' }}>
          {musicFiles.length > 0 && (
            <MusicSelector
              value={currentMusic || ''}
              onChange={(e) => {
                setCurrentMusic(e.target.value);
                if (isPlaying) {
                  audio.pause();
                  setIsPlaying(false);
                }
              }}
            >
              {musicFiles.map(music => (
                <option key={music} value={music}>
                  {music.replace(/\.(mp3|wav|m4a)$/i, '')}
                </option>
              ))}
            </MusicSelector>
          )}
          <MusicControl
            onClick={toggleMusic}
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
          >
            {isPlaying ? <FaPause /> : <FaPlay />}
          </MusicControl>
          <NavButton
            onClick={() => {
              fetchImages();
              fetchMusicFiles();
            }}
            disabled={isRefreshing}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            <FaSync style={{ animation: isRefreshing ? 'spin 1s linear infinite' : 'none' }} /> 
            {isRefreshing ? 'Refreshing...' : 'Refresh'}
          </NavButton>
          <NavButton
            onClick={() => navigate('/')}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            <FaHome /> Home
          </NavButton>
        </div>
      </Header>

      <GalleryGrid
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.5, delay: 0.2 }}
      >
        {images.map((image, index) => (
          <ImageCard
            key={image}
            layoutId={`image-${index}`}
            whileHover={{ scale: 1.02 }}
            transition={{ duration: 0.2 }}
          >
            <div style={{ position: 'relative', overflow: 'hidden', borderRadius: '15px' }}>
              <Image
                src={`http://localhost:8080/api/images/${image}`}
                alt={`Memory ${index + 1}`}
                loading="lazy"
                onClick={() => openLightbox(image, index)}
              />
              <ImageOverlay>
                <div>Memory #{index + 1}</div>
              </ImageOverlay>
              <PhotoInteractions photoName={image} show={true} />
            </div>
          </ImageCard>
        ))}
      </GalleryGrid>

      <AnimatePresence>
        {selectedImage && (
          <Lightbox
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            onClick={closeLightbox}
          >
            <LightboxControls>
              <LightboxButton onClick={closeLightbox}>
                <FaTimes />
              </LightboxButton>
            </LightboxControls>

            <NavigationButtons>
              <NavButtonLightbox onClick={prevImage}>
                <FaChevronLeft />
              </NavButtonLightbox>
              <NavButtonLightbox onClick={nextImage}>
                <FaChevronRight />
              </NavButtonLightbox>
            </NavigationButtons>

            <LightboxImage
              src={`http://localhost:8080/api/images/${images[currentIndex]}`}
              alt={`Memory ${currentIndex + 1}`}
              onClick={(e) => e.stopPropagation()}
            />
            <PhotoInteractions photoName={images[currentIndex]} show={true} />
          </Lightbox>
        )}
      </AnimatePresence>

      {images.length > 0 && (
        <FinalMessage
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 1, duration: 0.5 }}
        >
          Made with ❤️ by the one who loves you the most
        </FinalMessage>
      )}
    </GalleryContainer>
  );
};

export default Gallery;
