import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { FaHeart, FaComment, FaTimes, FaPaperPlane, FaTrash } from 'react-icons/fa';
import axios from 'axios';

const InteractionsContainer = styled.div`
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  background: linear-gradient(transparent, rgba(0, 0, 0, 0.8));
  padding: 20px;
  color: white;
  transform: translateY(100%);
  transition: transform 0.3s ease;

  ${({ show }) => show && `
    transform: translateY(0);
  `}

  @media (max-width: 768px) {
    padding: 15px;
  }
`;

const CommentsContainer = styled.div`
  position: relative;
  background: rgba(0, 0, 0, 0.9);
  border-radius: 15px;
  margin-top: 10px;
  padding: 20px;
  color: white;
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.1);

  @media (max-width: 768px) {
    padding: 15px;
    margin-top: 8px;
  }
`;

const InteractionButtons = styled.div`
  display: flex;
  gap: 15px;
  margin-bottom: 15px;

  @media (max-width: 768px) {
    gap: 10px;
    margin-bottom: 12px;
  }
`;

const InteractionButton = styled(motion.button)`
  background: rgba(255, 255, 255, 0.2);
  border: none;
  color: white;
  padding: 8px 12px;
  border-radius: 20px;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 5px;
  font-size: 0.9rem;
  backdrop-filter: blur(10px);
  transition: all 0.3s ease;

  &:hover {
    background: rgba(255, 255, 255, 0.3);
  }

  &.liked {
    background: #e74c3c;
    color: white;
  }

  @media (max-width: 768px) {
    padding: 10px 14px;
    font-size: 1rem;
    gap: 6px;
  }
`;

const LikeCount = styled.span`
  font-size: 0.8rem;
  opacity: 0.8;
`;

const CommentsSection = styled.div`
  max-height: 200px;
  overflow-y: auto;
  margin-bottom: 15px;

  @media (max-width: 768px) {
    max-height: 150px;
    margin-bottom: 12px;
  }
`;

const CommentItem = styled.div`
  background: rgba(255, 255, 255, 0.1);
  padding: 10px;
  border-radius: 10px;
  margin-bottom: 8px;
  backdrop-filter: blur(10px);
  position: relative;

  &:hover .delete-button {
    opacity: 1;
  }
`;

const CommentAuthor = styled.div`
  font-weight: 600;
  font-size: 0.8rem;
  margin-bottom: 4px;
  color: #ffd700;
`;

const CommentText = styled.div`
  font-size: 0.9rem;
  line-height: 1.4;
`;

const CommentTime = styled.div`
  font-size: 0.7rem;
  opacity: 0.6;
  margin-top: 4px;
`;

const DeleteButton = styled(motion.button)`
  position: absolute;
  top: 8px;
  right: 8px;
  background: rgba(231, 76, 60, 0.8);
  border: none;
  color: white;
  width: 24px;
  height: 24px;
  border-radius: 50%;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 0.7rem;
  opacity: 0;
  transition: all 0.3s ease;

  &:hover {
    background: #e74c3c;
    transform: scale(1.1);
  }

  @media (max-width: 768px) {
    opacity: 1;
    width: 28px;
    height: 28px;
    font-size: 0.8rem;
  }
`;

const CommentForm = styled.form`
  display: flex;
  gap: 8px;
  align-items: center;

  @media (max-width: 768px) {
    flex-direction: column;
    gap: 10px;
  }
`;

const CommentInput = styled.input`
  flex: 1;
  background: rgba(255, 255, 255, 0.2);
  border: 1px solid rgba(255, 255, 255, 0.3);
  color: white;
  padding: 12px 16px;
  border-radius: 25px;
  font-size: 1rem;
  backdrop-filter: blur(10px);
  min-width: 200px;

  &::placeholder {
    color: rgba(255, 255, 255, 0.7);
  }

  &:focus {
    outline: none;
    border-color: rgba(255, 255, 255, 0.5);
    background: rgba(255, 255, 255, 0.25);
  }

  @media (max-width: 768px) {
    width: 100%;
    min-width: unset;
    font-size: 16px; /* Prevents zoom on iOS */
  }
`;

const SendButton = styled(motion.button)`
  background: #e74c3c;
  border: none;
  color: white;
  width: 45px;
  height: 45px;
  border-radius: 50%;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.3s ease;
  font-size: 1.1rem;

  &:hover {
    background: #c0392b;
    transform: scale(1.1);
  }

  &:disabled {
    opacity: 0.5;
    cursor: not-allowed;
    transform: none;
  }

  @media (max-width: 768px) {
    width: 50px;
    height: 50px;
    font-size: 1.2rem;
  }
`;



const SuccessMessage = styled(motion.div)`
  background: #27ae60;
  color: white;
  padding: 8px 12px;
  border-radius: 10px;
  font-size: 0.8rem;
  text-align: center;
  margin-bottom: 10px;
  backdrop-filter: blur(10px);
`;

const PhotoInteractions = ({ photoName, show }) => {
  const [liked, setLiked] = useState(false);
  const [likeCount, setLikeCount] = useState(0);
  const [comments, setComments] = useState([]);
  const [showComments, setShowComments] = useState(false);
  const [newComment, setNewComment] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [showSuccess, setShowSuccess] = useState(false);

  // Default user name for comments
  const defaultUserName = 'Lover';

  useEffect(() => {
    if (photoName) {
      fetchLikeStatus();
      fetchLikeCount();
      fetchComments();
    }
  }, [photoName]);

  const fetchLikeStatus = async () => {
    try {
      const response = await axios.get(`http://localhost:8080/api/likes/${photoName}/user/${defaultUserName}`);
      setLiked(response.data);
    } catch (error) {
      console.error('Error fetching like status:', error);
    }
  };

  const fetchLikeCount = async () => {
    try {
      const response = await axios.get(`http://localhost:8080/api/likes/${photoName}`);
      setLikeCount(response.data);
    } catch (error) {
      console.error('Error fetching like count:', error);
    }
  };

  const fetchComments = async () => {
    try {
      const response = await axios.get(`http://localhost:8080/api/comments/${photoName}`);
      setComments(response.data);
    } catch (error) {
      console.error('Error fetching comments:', error);
    }
  };

  const handleLike = async () => {
    try {
      const response = await axios.post('http://localhost:8080/api/likes', {
        photoName: photoName,
        userName: defaultUserName
      });
      setLiked(response.data.liked);
      setLikeCount(response.data.likeCount);
    } catch (error) {
      console.error('Error toggling like:', error);
    }
  };

  const handleComment = async (e) => {
    e.preventDefault();
    console.log('Submitting comment:', { photoName, commentText: newComment });

    if (!newComment.trim()) {
      console.log('Comment is empty');
      return;
    }

    setIsSubmitting(true);
    try {
      const commentData = {
        photoName: photoName,
        commentText: newComment.trim(),
        authorName: defaultUserName
      };

      console.log('Sending comment data:', commentData);

      const response = await axios.post('http://localhost:8080/api/comments', commentData);

      console.log('Comment submitted successfully:', response.data);

      setComments([response.data, ...comments]);
      setNewComment('');
      setIsSubmitting(false);
      setShowSuccess(true);
      setTimeout(() => setShowSuccess(false), 3000);
    } catch (error) {
      console.error('Error adding comment:', error);
      console.error('Error details:', error.response?.data);
      alert('Failed to submit comment. Please try again.');
      setIsSubmitting(false);
    }
  };

  const handleDeleteComment = async (commentId) => {
    if (!window.confirm('Are you sure you want to delete this comment?')) {
      return;
    }

    try {
      await axios.delete(`http://localhost:8080/api/comments/${commentId}`);
      setComments(comments.filter(comment => comment.id !== commentId));
      console.log('Comment deleted successfully');
    } catch (error) {
      console.error('Error deleting comment:', error);
      alert('Failed to delete comment. Please try again.');
    }
  };

  const formatTime = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString() + ' ' + date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  return (
    <>
      <InteractionsContainer show={show}>
        <InteractionButtons>
          <InteractionButton
            onClick={handleLike}
            className={liked ? 'liked' : ''}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            <FaHeart style={{ color: liked ? '#fff' : '#e74c3c' }} />
            <span>Like</span>
            {likeCount > 0 && <LikeCount>({likeCount})</LikeCount>}
          </InteractionButton>

          <InteractionButton
            onClick={() => setShowComments(!showComments)}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            <FaComment />
            <span>Comment</span>
            {comments.length > 0 && <LikeCount>({comments.length})</LikeCount>}
          </InteractionButton>
        </InteractionButtons>
      </InteractionsContainer>

      {showComments && (
        <CommentsContainer>
          <CommentsSection>
            {comments.map((comment) => (
              <CommentItem key={comment.id}>
                <DeleteButton
                  className="delete-button"
                  onClick={() => handleDeleteComment(comment.id)}
                  whileHover={{ scale: 1.1 }}
                  whileTap={{ scale: 0.9 }}
                  title="Delete comment"
                >
                  <FaTrash />
                </DeleteButton>
                <CommentAuthor>{comment.authorName}</CommentAuthor>
                <CommentText>{comment.commentText}</CommentText>
                <CommentTime>{formatTime(comment.createdAt)}</CommentTime>
              </CommentItem>
            ))}
            {comments.length === 0 && (
              <div style={{ textAlign: 'center', opacity: 0.7, fontStyle: 'italic' }}>
                No comments yet. Be the first to comment! üíï
              </div>
            )}
          </CommentsSection>

          {showSuccess && (
            <SuccessMessage
              initial={{ opacity: 0, y: -10 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -10 }}
            >
              üíï Comment added successfully!
            </SuccessMessage>
          )}

          <CommentForm onSubmit={handleComment}>
            <CommentInput
              type="text"
              placeholder="Write a romantic comment... üíï"
              value={newComment}
              onChange={(e) => setNewComment(e.target.value)}
              required
            />
            <SendButton
              type="submit"
              disabled={isSubmitting || !newComment.trim()}
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.9 }}
            >
              {isSubmitting ? (
                <motion.div
                  animate={{ rotate: 360 }}
                  transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
                >
                  ‚è≥
                </motion.div>
              ) : (
                <FaPaperPlane />
              )}
            </SendButton>
          </CommentForm>
        </CommentsContainer>
      )}
    </>
  );
};

export default PhotoInteractions;
